@import url('https://fonts.googleapis.com/css2?family=Reem+Kufi&display=swap');

.header {
  @include borderTest;
  @include flexCentered;
  //   opacity: 0;
  background: rgba(250, 250, 250, 0);
  position: fixed;
  width: 100%;
  justify-content: space-around;
  font-family: 'Reem Kufi', sans-serif;
  padding-top: 2em;
  &__logo {
    @include borderTest;
    width: 150px;
    margin-left: 2%;
  }

  nav {
    @include borderTest;
    width: 60%;
    height: 5em;

    @include flexCentered;
  }
  &__nav-container {
    @include flexCentered;
    font-size: 1.4em;
  }

  &__nav {
    @include borderTest;
    padding: 1em;
    margin: 0 1em;
  }

  #navBtn__sign-in {
    margin-right: 8em;
  }
}

