sources.sass\\_testimonialSection.scss:
.testimonial {
  height: 100vh;
  color: $color-para;
  padding: 11em 6em 5em 6em;
  margin-top: 2em;
  // @include sectionBorderTest;
  &__heading {
    font-size: $fontSize-Lm;
    margin-bottom: 1em;
  }

  &__sub-heading {
    font-size: $fontSize-Sm;
  }

  &__cards {
    padding: 1em;
    height: 40vh;
    position: relative;
    overflow-x: hidden;
    width: 90vw;
  }

  &__card {
    border: 1px solid silver;
    padding: 1em;
    text-align: center;
    width: 44em;
    height: 28em;
    border-radius: 1.4em;
    margin: 1em;
    // position: absolute;
  }

  &__image {
    width: 8em;
    height: 8em;
    background-size: cover;
    background-position: center;
    border-radius: 50%;
    display: inline-block;

    &.A {
      background-image: url('../src/Testimonial/AllefVinicius.jpg');
    }
    &.B {
      background-image: url('../src/Testimonial/ErikLucatero.jpg');
    }
    &.C {
      background-image: url('../src/Testimonial/GabrielSilverio.jpg');
    }
    &.D {
      background-image: url('../src/Testimonial/JosephGonzalez.jpg');
    }
    &.E {
      background-image: url('../src/Testimonial/PetrSevcovic.jpg');
    }
    &.F {
      background-image: url('../src/Testimonial/TalenDeStCroix.jpg');
    }
  }

  &__text {
    font-size: 1.6rem;
    text-align: center;
    padding: 1em;
    border-bottom: 1px solid white;
    margin-bottom: 1em;
    &::after,
    &::before {
      content: '"';
    }
  }

  &__review {
    @include flexCentered;
    justify-content: space-between;
    width: 90%;
  }

  &__name,
  &__rating-number {
    font-size: $fontSize-SS;
  }

  .stars {
    .star {
      width: 24px;
      //   border: $borderTest;
      font-size: x-large;
      color: rgb(128, 128, 128);
      display: inline-block;

      &:before {
        content: '\2605';
      }

      &.on {
        color: $color-star;
      }

      &.half:after {
        content: '\2605';
        color: $color-star;
        position: absolute;
        margin-left: -20px;
        overflow-x: hidden;
        width: 10px;
      }
    }
  }

  &__slide {
    display: flex;
    justify-content: center;
    align-items: center;
    position: absolute;
    transition: transform 500ms ease-in-out 200ms;
    &.A {
      transform: translateX(-140em);
      //   margin-right: 1em;
    }

    &.B {
      transform: translateX(0);
    }

    &-navigator {
      &-container {
        padding: 4em;
      }
      font-size: $fontSize-mL;
      font-weight: bold;
      background-color: whitesmoke;
      color: black;
      border-radius: 50%;
      padding: 0.1em 0.5em;
      text-align: center;
      &:hover {
        background-color: #026cd7;
        cursor: pointer;
        color: whitesmoke;
        font-size: $fontSize-mL + 0.1rem;
      }

      &:active {
        background-color: rgba(1, 79, 156, 0.904);
        color: whitesmoke;
        font-size: $fontSize-mL + 0.1rem;
      }
    }

    &-dot {
      display: inline-block;
      margin: 0 0.5em;
      height: 16px;
      width: 16px;
      background: rgb(192, 189, 189);
      border-radius: 50%;

      &.active {
        border: 3px solid rgba($color: #0367ebc2, $alpha: 0.6);
      }
    }
  }
}

.background__circle {
  background: linear-gradient(
    318deg,
    rgba(113, 2, 133, 0.61),
    rgba(13, 78, 168, 0.81),
    rgba(0, 166, 175, 0.87)
  );
  width: 28em;
  height: 28em;
  border-radius: 50%;
  @include flexCentered;
  position: absolute;
  right: -10em;
  bottom: -340em;
  z-index: 10;
  &-background {
    position: relative;
    background: linear-gradient(rgb(5, 13, 33), rgb(5, 13, 33), rgb(5, 13, 33));
    width: 98%;
    height: 98%;
    border-radius: 50%;
  }
}

