sources.sass\\_base.scss:
@import url('https://fonts.googleapis.com/css2?family=Red+Hat+Text&display=swap');

* {
  margin: 0;
  padding: 0;
}

*,
*::after,
*::before {
  box-sizing: inherit;
}

html {
  box-sizing: border-box;
  font-size: 62.5%;
  @media screen and (max-width: 675px) {
    .hero__header,
    .big__corp.header,
    .pricing__header,
    .bottom__newsletter-header {
      font-size: $fontSize-h1 - 1rem;
    }

    h2 {
      font-size: $fontSize-h2 - 1rem;
    }
  }
}

body {
  font-family: 'Red Hat Text', sans-serif;
  text-align: center;
  overflow-x: hidden;
}

a {
  text-decoration: none;
}

li {
  list-style-type: none;
}

.btn__sign-in {
  padding: 0.8em;
  width: 11em;
  font-family: Verdana, Geneva, Tahoma, sans-serif;
  font-weight: 500;
  color: ivory;
  background-color: #0b233b;
  border-radius: 8px;
  font-family: 'Reem Kufi', sans-serif;
  &:hover {
    background-color: #0a5cad;
    padding: 1em;
  }
}

button.hero__btn-vdo {
  padding: 0.8em;
  width: 11em;
  font-weight: 600;
  letter-spacing: 0.8px;
  color: #026cd7;
  background-color: $color-heading;
  border-radius: 8px;
  font-family: 'Reem Kufi', sans-serif;
  &:hover {
    background-color: #026cd7;
    color: white;
    padding: 1em;
  }
}

.content_container {
  background-color: $primary;
}

sources.sass\\_navbar.scss:
@import 'helper';
@import url('https://fonts.googleapis.com/css2?family=Reem+Kufi&display=swap');

.header {
  // @include borderTest;
  @include flexCentered;
  height: 10vh;
  width: 100%;
  justify-content: space-between;
  font-family: 'Reem Kufi', sans-serif;
  padding: 1em;
  &__logo {
    // @include borderTest;
    width: 100px;
    margin-left: 2%;
  }

  ul > li > a {
    color: rgb(233, 232, 232);
    font-weight: 500;
    @include transition-ease;
    &:hover {
      color: rgb(252, 127, 185);
      font-size: $fontSize-Sm;
    }
  }

  &__menu-container {
    // @include borderTest;
    width: 60%;
    height: 5em;
    @include flexCentered;
    @media screen and (max-width: 783px) {
      transform: translateX(-25%);
    }
    @media screen and (max-width: 680px) {
      width: 100vw;
      transform: translateX(0);
      position: absolute;
      left: 0;
    }
  }
  &__menu__li-container {
    @include flexCentered;
    font-size: 1.4em;
    @include transition-ease;
    @media screen and (max-width: 680px) {
      display: none;
    }
  }

  &__menu {
    // @include borderTest;
    padding: 1em;
    // margin: 0 1em;
    @include transition-ease;
  }

  #navBtn__sign-in {
    margin-right: 3em;

    @media screen and (max-width: 830px) {
      margin-right: 1em;
      width: 9em;
    }
    @media screen and (max-width: 783px) {
      display: none;
    }
  }

  &__menubars-container {
    width: 4em;
    height: 5em;
    padding: 3em;
    display: none;
    // border: $borderTest;
    @include transition-ease;
    &:hover {
      border: 1px solid slateblue;
      border-radius: 20%;
      background: rgba(224, 224, 224, 0.192);
    }
    @include transition-ease;

    @media screen and (max-width: 680px) {
      display: inline-grid;
      position: absolute;
      // justify-content: end;
      right: 4%;
      height: 5vh;
      top: 0;
      padding: 0.5em;
      justify-content: center;
      align-items: center;
    }
  }

  &__menubars {
    &_top,
    &_middle,
    &_bottom {
      width: 3em;
      height: 0.4em;
      background: rgb(226, 226, 226);
      // display: inline-block;
      border-radius: 0.3em;
      @include transition-ease;
    }
  }

  @media screen and (max-width: 380px) {
    padding: 1em 1em 0 0;
  }

  &.open {
    div.header__menubars_top {
      transform: rotate(45deg);
      position: relative;
      top: 16px;
      margin-bottom: 1em;
    }
    div.header__menubars_middle {
      display: none;
    }
    div.header__menubars_bottom {
      transform: rotate(-45deg);
      margin-bottom: 1em;
    }

    ul.header__menu__li-container {
      display: block;
      position: absolute;
      top: 0;
      left: 0;
      width: 100vw;
      z-index: 10;
      background: #050b18;
      height: 60vh;
      padding: 5em 0 0 0;
      li {
        background: #050b18;
        width: 100%;
        font-size: 1.8rem;
        padding: 1.2em 0;

        &:hover {
          background: linear-gradient(166deg, #005c75, rgb(1, 60, 122));
          // transform: scale(1.1);
          box-shadow: 0px 1px 8px 1px rgba(239, 239, 239, 0.308);
        }
      }
    }
    .header__menubars-container {
      margin-bottom: 1em;
      border-radius: 50%;

      &:hover {
        border: none;
        border-radius: 0;
        background-color: transparent;
      }

      z-index: 20;
    }

    .header__menu-container {
      top: 0;
    }
  }
}

sources.sass\\heroSection.scss:
@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@600&display=swap');

.hero {
  height: 90vh;
  position: relative;
  z-index: 20;
  background: linear-gradient(
    295.61deg,
    rgb(142, 72, 155),
    rgba(16, 60, 120),
    rgba(0, 166, 176, 0.94)
  );
  .hero__content {
    height: 90vh;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    width: 100%;
  }

  &__header {
    font-size: $fontSize-h1;
    font-family: 'Poppins', sans-serif;
    letter-spacing: 1px;
    margin-bottom: 0.4em;
    margin-top: -2em;
    color: $color-heading;
    width: 70vw;
    @media screen and (max-width: 540px) {
      width: 90vw;
    }
  }

  &__sub-header {
    font-size: $fontSize-h2;
    line-height: 3rem;
    margin-bottom: 2em;
    font-weight: 500;
    width: 60vw;
    color: #e9e9e7;
    span {
      font-weight: bold;
      color: rgb(221, 233, 243);
    }

    @media screen and (max-width: 500px) {
      font-size: 1.6rem;
      width: 80vw;
      // background-color: black;
    }
  }

  &__btn-container {
    margin-bottom: 18em;
  }

  button#hero__btn-vdo {
    margin-left: 2em;
  }

  &__image {
    width: 50%;
    min-width: 50em;
    display: inline-block;
    position: absolute;
    top: 70%;

    @media screen and (max-width: 500px) {
      min-width: 80%;
      top: 80%;
    }
  }

  @media screen and (max-width: 360px) {
    h1 {
      font-size: $fontSize-Sm;
    }
    &__sub-header {
      font-size: $fontSize-S;
    }
    &__btn-container {
      margin-bottom: 21em;
    }
    .btn__sign-in {
      padding: 0.6em;
      width: 9em;
      font-size: 1.1em;
    }

    .hero__btn-vdo {
      margin-left: 2em;
      font-size: 1.1em;
      width: 10em;
      padding: 0.6em;
    }
  }
}

sources.sass\\_featuresSection.scss:
@import url('https://fonts.googleapis.com/css2?family=Parisienne&display=swap');

.features {
  height: 110vh;
  // background-color: blue;
  padding: 4em;
  position: relative;
  @include flexCentered;
  // @include sectionBorderTest;
  &__heading {
    color: $color-heading;
    font-size: $fontSize-h2;
    letter-spacing: 2px;
  }

  &__text {
    width: 50%;
  }
  &__text-heading {
    color: $color-heading;
    letter-spacing: 1.2px;
  }

  &__text-text {
    color: $color-para;
    // border: $borderTest;
    font-size: 1.4rem;
  }

  &__text-card {
    @include flexCentered;
    align-items: start;
    // border: $borderTest;
    margin: 1.5em;
  }

  &__text-icon {
    // border: $borderTest;
    width: 4em;
    height: 4em;
    padding: 1em;
    text-align: center;
    border-radius: 50%;
    background-color: #0663c0;

    #more {
      height: 1.8em;

      //   width: 2em;
    }
  }

  .Emanager_font {
    color: #dcdcdc;
    font-family: 'Parisienne', cursive;
    font-size: 1.8em;
  }

  &__text-container {
    padding: 0.2em 0 1em 1em;
    line-height: 1.8em;
    font-size: 1.2rem;
    letter-spacing: 0.7px;
    width: 80%;
    text-align: start;
  }

  &__text-heading {
    margin-bottom: 1em;
  }

  &__more_bold-text {
    font-weight: bold;
  }
  &__decoration {
    // border: $borderTest;

    width: 50%;
    height: 100%;
    position: static;
    z-index: 20;
    @include flexCentered;
    &__image-container {
      display: grid;
      grid-template-columns: 1fr 1fr;
      padding: 2em;
      width: auto;
      position: absolute;
      z-index: 30;

      // @media screen and (max-width: 790px) {
      //   grid-template-columns: 1fr;
      // }
    }
    &-image {
      // width: 23em;
      width: 70%;
      margin: 1em 0;

      @media screen and (max-width: 960px) {
        width: 100%;
      }
    }
    // #splitCard {
    //   width: 25em;
    // }

    &-gradient {
      width: 70%;
      height: 100%;
      background: linear-gradient(
        295.61deg,
        rgb(142, 72, 155),
        rgba(16, 60, 120),
        rgba(0, 166, 176, 0.94)
      );
      border-radius: 0.8em;
      position: relative;
      right: -11%;
    }
  }

  @media screen and (max-width: 680px) {
    flex-direction: column;
    height: 200vh;
    padding: 2em;
    &__text {
      width: 100%;
      margin-bottom: 8em;
    }

    &__decoration {
      width: 100%;
      height: 60vh;
      padding: 1em;
      &__image-container {
        // grid-template-columns: 1fr 1fr;
        grid-template-columns: minmax(120px, 1fr) minmax(120px, 1fr);
        width: 80%;
      }

      &-gradient {
        width: 100%;
        position: static;
      }
    }
  }

  @media screen and (max-width: 380px) {
    height: max-content;
    padding: 0em;
    // font-size: $fontSize-Sm;

    &__heading {
      font-size: $fontSize-Sm;
    }
    &__text {
      margin-bottom: 5%;

      &-card {
        margin: 1em;
      }
      &-icon {
        width: 3em;
        height: 3em;
        padding: 0.7em;
        > img {
          width: 15px;
          height: 15px;
        }
      }
    }

    // &__decoration__image-container{
    //   width: ;
    // }
    &__decoration-image {
      width: 100%;
    }
  }
}

sources.sass\\_helper.scss:
@mixin flexCentered {
  display: flex;
  display: -webkit-flex;
  display: -moz-flex;
  display: -ms-flex;
  justify-content: center;
  align-items: center;
}

@mixin borderTest {
  border: 1px solid rgb(250, 151, 151);
}

@mixin sectionBorderTest {
  border-top: 2px solid khaki;
  border-bottom: 2px solid khaki;
}

@mixin transition-ease {
  transition: all 400ms ease-in-out;
}
@mixin transition-fast {
  transition: all 200ms ease-in-out;
}
//background color
$primary: #050b18;

$color-heading: #fefefb;
$color-para: #dfdfdf;
$color-dark: rgb(22, 22, 22);
$borderTest: 1px solid khaki;

// font size

$fontSize-h1: 4rem;
$fontSize-h2: 2rem;
$fontSize-h1-m: 3rem;
$fontSize-mL: 2.5rem;
$fontSize-Lm: 2.4rem;
$fontSize-m: 2rem;
$fontSize-Sm: 1.6rem;
$fontSize-S: 1.4rem;
$fontSize-SS: 1.2rem;

// star color of the reviews

$color-star: rgb(231, 198, 8);

