sources.sass\\_pricingSection.scss:
$btn-bg: #0569cd;

.pricing {
  height: 100vh;
  padding: 6em;
  margin-top: 6em;
  // @include sectionBorderTest;
  &__header {
    color: $color-heading;
    font-size: $fontSize-h1-m;
    margin-bottom: 1em;
  }

  &__sub-heading {
    color: $color-para;
    font-size: $fontSize-h2;
  }

  table,
  tr,
  td {
    margin: 0;
    padding: 0;
    border: none;
    border-collapse: collapse;
    border-spacing: 0;
  }
  tr,
  td {
    // border: $borderTest;
    border-top: 1em solid transparent;
    border-bottom: 1em solid transparent;
  }

  &__cards {
    // border: $borderTest;
    @include flexCentered;
  }

  &__card {
    border: 2px solid #464952;
    border-radius: 0.6em;
    padding: 2em;
    // width: 40em;
    width: 100%;
    height: 70vh;
    margin: 3em 2em;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    @include transition-ease;
    &:hover {
      background-color: $btn-bg;
      height: 71vh;

      .pricing__card__btn {
        background-color: rgb(233, 233, 233);
        color: rgb(54, 54, 54);
      }
      .pricing__card__icon-check_container {
        color: rgb(1, 138, 31);
      }
    }
    &-heading {
      color: $color-heading;
      margin-bottom: 1em;
      font-size: $fontSize-mL;
    }

    &__sub-heading {
      color: $color-para;
      font-size: $fontSize-Sm;

      &__price {
        font-weight: bold;
        font-size: $fontSize-Lm;
      }
    }

    & .container {
      padding: 1em;
      // background-color: lawngreen;
      width: 100%;
      min-width: 15em;
      color: $color-para;
    }
    &-service {
      color: $color-para;
      padding: 0.8em;
      font-size: $fontSize-Sm;
    }

    &__icon-check {
      font-size: $fontSize-Sm;
      font-weight: bolder;
      text-align: center;
      &_container {
        background: rgb(236, 235, 235);
        border-radius: 50%;
        padding: 0.1em 0.4em;
        color: rgb(39, 39, 39);
        margin-right: 1em;
      }
    }

    &__btn {
      padding: 0.8em;
      width: 11em;
      font-family: Verdana, Geneva, Tahoma, sans-serif;
      font-weight: 500;
      color: ivory;
      background-color: $btn-bg;
      border-radius: 8px;
      font-family: 'Reem Kufi', sans-serif;
      border: 0;
      @include transition-fast;
      &:hover {
        color: rgb(15, 15, 15);
        font-weight: bold;
        border: 4px solid rgba(7, 117, 207, 0.616);
      }
    }
  }

  @media screen and (max-width: 820px) {
    // margin-top: 15em;
  }
  @media screen and (max-width: 680px) {
    &__cards {
      flex-direction: column;
    }
    div#card-special {
      .pricing__card-heading {
        margin-bottom: 0;
      }
      .pricing__card.container {
        padding: 0;
      }
      // .pricing__card {
      //   font-size: 1.2rem;
      // }
    }
  }
}

sources.sass\\_testimonialSection.scss:
.testimonial {
  height: 100vh;
  color: $color-para;
  padding: 11em 6em 5em 6em;
  margin-top: 2em;
  // @include sectionBorderTest;

  &__content {
    position: static;
    z-index: 20;
  }
  &__heading {
    font-size: $fontSize-Lm;
    margin-bottom: 1em;
  }

  &__sub-heading {
    font-size: $fontSize-Sm;
  }

  &__cards {
    padding: 1em;
    height: 40vh;
    position: relative;
    overflow-x: hidden;
    width: 90vw;

    @media screen and (max-width: 1530px) {
      height: 60vh;
    }
    @media screen and (max-width: 680px) {
      height: 78vh;
    }
  }

  &__card {
    border: 1px solid rgb(196, 196, 196);
    padding: 1em;
    text-align: center;
    // width: 44em;
    width: 100%;
    max-width: 44em;
    height: 28em;
    border-radius: 1.4em;
    margin: 1em;
    background: linear-gradient(
      283deg,
      rgba(29, 29, 29, 0.8),
      rgba(226, 225, 225, 0.38)
    );
    @media screen and (max-width: 480px) {
      height: auto;
      margin: 1.8em 0;
    }
  }

  &__image {
    width: 8em;
    height: 8em;
    background-size: cover;
    background-position: center;
    border-radius: 50%;
    display: inline-block;
    position: relative;
    top: -3em;
    left: -14em;
    &.A {
      background-image: url('../src/Testimonial/AllefVinicius.jpg');
    }
    &.B {
      background-image: url('../src/Testimonial/ErikLucatero.jpg');
    }
    &.C {
      background-image: url('../src/Testimonial/GabrielSilverio.jpg');
    }
    &.D {
      background-image: url('../src/Testimonial/JosephGonzalez.jpg');
    }
    &.E {
      background-image: url('../src/Testimonial/PetrSevcovic.jpg');
    }
    &.F {
      background-image: url('../src/Testimonial/TalenDeStCroix.jpg');
    }

    @media screen and (max-width: 480px) {
      left: 0;
    }
  }

  &__text {
    font-size: 1.6rem;
    padding: 0 1em 1em 1em;
    border-bottom: 1px solid white;
    margin-bottom: 1em;
    &::after,
    &::before {
      content: '"';
    }
  }

  &__review {
    @include flexCentered;
    justify-content: space-between;
    width: 90%;
  }

  &__name,
  &__rating-number {
    font-size: $fontSize-SS;
    font-weight: bold;
    letter-spacing: 1px;
    margin-right: 0.5em;
  }

  .stars {
    @include flexCentered;
    .star {
      width: 24px;
      //   border: $borderTest;
      font-size: x-large;
      color: rgb(128, 128, 128);
      display: inline-block;

      &:before {
        content: '\2605';
      }

      &.on {
        color: $color-star;
      }

      &.half:after {
        content: '\2605';
        color: $color-star;
        position: absolute;
        margin-left: -20px;
        overflow-x: hidden;
        width: 10px;
      }
    }
  }
  &__slide,
  &__slide-A,
  &__slide-B {
    @include flexCentered;

    flex-wrap: wrap;
    position: absolute;
    transition: transform 500ms ease-in-out 200ms;
    &-A {
      transform: translateX(0);

      &.activeA {
        transform: translateX(0);
      }

      &.removeA {
        transform: translateX(-100vw);
      }
    }

    &-B {
      transform: translateX(100vw);
      &.activeB {
        transform: translateX(0);
      }
    }

    &-navigator__left,
    &-navigator__right {
      &-container {
        padding: 4em;
      }
      font-size: $fontSize-mL;
      font-weight: bold;
      background-color: whitesmoke;
      color: black;
      border-radius: 50%;
      padding: 0.1em 0.5em;
      text-align: center;
      @include transition-ease;
      &:hover {
        background-color: #026cd7;
        cursor: pointer;
        color: whitesmoke;
        font-size: $fontSize-mL + 0.1rem;
      }

      &:active {
        background-color: rgba(1, 79, 156, 0.904);
        color: whitesmoke;
        font-size: $fontSize-mL + 0.1rem;
      }
    }

    &-dot__left,
    &-dot__right {
      display: inline-block;
      margin: 0 0.5em;
      height: 16px;
      width: 16px;
      background: rgb(192, 189, 189);
      border-radius: 50%;

      &.active {
        border: 3px solid rgba($color: #0367ebc2, $alpha: 0.6);
        @include transition-ease;
      }
    }
  }

  &__background__circle {
    background: linear-gradient(
      318deg,
      rgba(113, 2, 133, 0.61),
      rgba(13, 78, 168, 0.81),
      rgba(0, 166, 175, 0.87)
    );
    width: 28em;
    height: 28em;
    border-radius: 50%;
    @include flexCentered;
    position: absolute;
    // position: relative;
    right: -12em;
    bottom: -339em;
    z-index: 10;
    &-background {
      position: relative;
      background: linear-gradient(
        rgb(5, 13, 33),
        rgb(5, 13, 33),
        rgb(5, 13, 33)
      );
      width: 98%;
      height: 98%;
      border-radius: 50%;
    }
  }

  @media screen and (max-width: 680px) {
    // height: 200vh;
    // margin-top: 127em;
    margin-top: 150em;
    padding: 0 0 5em 1em;

    &__background__circle {
      display: none;
    }

    .testimonial__slide-navigator-container {
      margin-top: 3em;
    }
  }
}

sources.sass\\_bottomSection.scss:
.bottom {
  height: 130vh;
  // @include sectionBorderTest;
  position: relative;
  z-index: 20;

  &__newsletter {
    @include flexCentered;
    background: linear-gradient(
      295.61deg,
      rgb(142, 72, 155),
      rgba(16, 60, 120),
      rgba(0, 166, 176, 0.94)
    );
    padding: 6em;
    width: 100%;
    &-content {
      @include flexCentered;
      flex-direction: column;
      width: 100%;
    }
    &.container {
      color: $color-para;
      width: 100%;
      height: 50vh;
      padding: 1em;
    }

    &-header {
      font-size: $fontSize-h1;
      font-family: 'Poppins', sans-serif;
      letter-spacing: 1px;
      margin-bottom: 0.4em;
      margin-top: -2em;
      color: $color-heading;
      //   width: 52vw;
      @media screen and (max-width: 500px) {
        font-size: $fontSize-mL !important;
      }
    }

    &__sub-heading {
      font-size: $fontSize-h2;
      line-height: 3rem;
      color: #e9e9e7;
      @media screen and (max-width: 500px) {
        font-size: $fontSize-Sm;
      }
    }

    &-input {
      @include flexCentered;
      margin-top: 2em;
      // border: 1px solid black;
      height: 5em;
      padding: 1em;
      background-color: #f7f7f7;
      border-radius: 0.8em;
      // width: 100%;
      input {
        padding: 0.2em;
        width: 25em;
        background-color: #f7f7f7;
        color: rgb(51, 50, 50);
        border: none;
        font-weight: 600;
        &:focus {
          outline: 0;
        }
      }

      &__btn {
        padding: 0.8em;
        // position: absolute;
        // left: 45em;
        background: linear-gradient(
          154deg,
          #27282b 17.69%,
          rgb(75, 75, 75) 71.8%
        );
        border-radius: 0.4em;
        border: none;
        color: $color-para;
        &:hover {
          background: linear-gradient(154deg, #303030 17.69%, #6b7588 81.8%);
          padding: 0.8em;
        }
        &:active {
          background: linear-gradient(154deg, #3f3f3f 17%, #824d8a 90.8%);
        }
      }

      @media screen and (max-width: 500px) {
        width: 100%;
      }
    }
  }

  .footer {
    // height: 0vh;
    &__nav-container {
      // @include flexCentered;
      display: table;
      width: 100%;
    }
    &__top-container {
      @include flexCentered;
      justify-content: space-between;
      height: 100%;
      padding: 7em 0 7em 5em;
      display: table;
    }

    &__left {
      width: 50%;
      color: $color-para;
      text-align: start;
      // border: $borderTest;
      display: table-cell;
      &-text {
        font-size: $fontSize-S;
        width: 90%;
        margin-left: 2%;
      }
      &-logo {
        width: 86px;
        margin-bottom: 1em;
      }

      &-social-icon-container {
        @include flexCentered;
        justify-content: flex-start;
        font-size: $fontSize-m;

        i {
          color: whitesmoke;
          @include transition-fast;
          &:hover {
            color: skyblue;
            // font-size: $fontSize-mL;
            transform: scale(2);
          }
        }
      }

      &-social-icon {
        padding: 1em;
      }
    }

    &__middle {
      color: $color-para;
      // border: $borderTest;
      font-size: $fontSize-S;
      // @include flexCentered;
      // justify-content: space-around;
      display: table;
      width: 83%;
      &__company {
        display: table-cell;
      }
      &__company__right {
        display: table-cell;
      }
    }

    a {
      color: $color-para;
    }

    ul > li > a:hover {
      color: skyblue;
      font-size: 1.3rem;
    }
    &__middle-nav {
      padding: 0.5em 0;
    }

    &__right__download {
      display: table-cell;
      width: 20%;
      &.content {
        width: 100%;
      }

      &-header {
        color: $color-para;
        padding: 1em 0;
        font-size: $fontSize-S;
      }

      &-apple {
        #apple {
          @include flexCentered;
          // background-color: rgb(137, 138, 139);
          background: linear-gradient(120.21deg, #babdc2, rgb(104, 107, 114));
          width: 15em;
          padding: 1em;
          border-radius: 0.4em;
          color: $color-dark;
          border: 2px solid rgb(190, 190, 190);
          &:hover {
            background: linear-gradient(120.21deg, #cacaca, rgb(97, 107, 133));
            border-right-color: rgba(187, 215, 226, 0.438);
          }
          &:active {
            background: linear-gradient(120.21deg, #e2b7b7, rgb(175, 82, 139));
          }
        }
        #apple-logo {
          font-size: $fontSize-m;
          padding-right: 4px;
        }

        &-text {
          display: inline-block;
          // border: $borderTest;
          font-weight: 600;
        }
      }

      &-playstore {
        padding-top: 1.5em;
        #google-play {
          @include flexCentered;
          background: linear-gradient(120.21deg, #babdc2, rgb(104, 107, 114));
          width: 15em;
          padding: 1em;
          border-radius: 0.4em;
          color: $color-dark;
          border: 2px solid rgb(190, 190, 190);

          &:hover {
            background: linear-gradient(120.21deg, #cacaca, rgb(97, 107, 133));
            border-right-color: rgba(187, 215, 226, 0.438);
          }
          &:active {
            background: linear-gradient(120.21deg, #e2b7b7, rgb(175, 82, 139));
          }
        }

        #google-play-logo {
          font-size: 2rem;
          padding-right: 4px;
        }
      }
    }

    &__copyright {
      color: $color-para;
      height: 10vh;
      font-size: $fontSize-S;
      @include flexCentered;
      border-top: 2px solid whitesmoke;
      padding: 5em;
    }

    @media screen and (max-width: 790px) {
      &__nav-container {
        @include flexCentered;
        flex-direction: column;
      }

      &__right__download__btn-container {
        @include flexCentered;
      }
      &__right__download {
        @include flexCentered;
        flex-direction: column;
        width: 100%;
        padding-bottom: 2em;
      }
      &__right__download-playstore {
        padding: 0;
        margin-left: 1em;
      }
    }

    @media screen and (max-width: 580px) {
      &__left-logo {
        width: 70px;
      }
      &__top-container {
        padding: 2em;
      }

      &__left-text {
        padding: 1em;
      }
      &__middle {
        width: 100%;
        padding-top: 1em;
      }
    }

    @media screen and (max-width: 470px) {
      &__top-container {
        @include flexCentered;
        flex-direction: column-reverse;
        // background-color: blueviolet;
      }

      &__left {
        width: 100%;
        text-align: center;
        &-social-icon-container {
          justify-content: center;
        }
      }
    }
  }

  @media screen and (max-width: 790px) {
    // height: 124vh;
  }
  @media screen and (max-width: 470px) {
    height: 137vh;
  }
}
