  }

  &__sub-heading {
    color: $color-para;
    font-size: $fontSize-h2;
  }

  table,
  tr,
  td {
    margin: 0;
    padding: 0;
    border: none;
    border-collapse: collapse;
    border-spacing: 0;
  }
  tr,
  td {
    // border: $borderTest;
    border-top: 1em solid transparent;
    border-bottom: 1em solid transparent;
  }

  &__cards {
    // border: $borderTest;
    @include flexCentered;
  }

  &__card {
    border: 2px solid #464952;
    border-radius: 0.6em;
    padding: 2em;
    width: 40em;
    height: 80vh;
    margin: 3em 2em;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    &-heading {
      color: $color-heading;
      margin-bottom: 1em;
      font-size: $fontSize-mL;
    }

    &__sub-heading {
      color: $color-para;
      font-size: $fontSize-Sm;

      &__price {
        font-weight: bold;
        font-size: $fontSize-Lm;
      }
    }

    & .container {
      padding: 1em;
      // background-color: lawngreen;
      width: 100%;
      min-width: 15em;
      color: $color-para;
    }
    &-service {
      color: $color-para;
      padding: 0.8em;
      font-size: $fontSize-Sm;
    }

    &__icon-check {
      font-size: $fontSize-Sm;
      font-weight: bolder;
      text-align: center;
      &_container {
        background: rgb(236, 235, 235);
        border-radius: 50%;
        padding: 0.2em;
        color: rgb(39, 39, 39);
        margin-right: 1em;
      }
    }

    &__btn {
      padding: 0.8em;
      width: 11em;
      font-family: Verdana, Geneva, Tahoma, sans-serif;
      font-weight: 500;
      color: ivory;
      background-color: #0569cd;
      border-radius: 8px;
      font-family: 'Reem Kufi', sans-serif;
      border: 0;
    }
  }
}

sources.sass\\_testimonialSection.scss:
.testimonial {
  height: 100vh;
  color: $color-para;
  padding: 6em;
  @include sectionBorderTest;
  &__heading {
    font-size: $fontSize-Lm;
    margin-bottom: 1em;
  }

  &__sub-heading {
    font-size: $fontSize-Sm;
  }

  &__cards {
    border: 1px solid khaki;
    padding: 1em;
    height: 40vh;
    position: relative;
    overflow-x: hidden;
    width: 90vw;
  }

  &__card {
    border: 1px solid silver;
    padding: 1em;
    text-align: center;
    width: 44em;
    height: 28em;
    border-radius: 1.4em;
    margin: 1em;
    // position: absolute;
  }

  &__image {
    width: 8em;
    height: 8em;
    background-size: cover;
    background-position: center;
    border-radius: 50%;
    display: inline-block;

    &.A {
      background-image: url('../src/Testimonial/AllefVinicius.jpg');
    }
    &.B {
      background-image: url('../src/Testimonial/ErikLucatero.jpg');
    }
    &.C {
      background-image: url('../src/Testimonial/GabrielSilverio.jpg');
    }
    &.D {
      background-image: url('../src/Testimonial/JosephGonzalez.jpg');
    }
    &.E {
      background-image: url('../src/Testimonial/PetrSevcovic.jpg');
    }
    &.F {
      background-image: url('../src/Testimonial/TalenDeStCroix.jpg');
    }
  }

  &__text {
    font-size: 1.6rem;
    text-align: center;
    padding: 1em;
    border-bottom: 1px solid white;
    margin-bottom: 1em;
    &::after,
    &::before {
      content: '"';
    }
  }

  &__review {
    @include flexCentered;
    justify-content: space-between;
    width: 90%;
  }

  &__name,
  &__rating-number {
    font-size: $fontSize-SS;
  }

  .stars {
    .star {
      width: 24px;
      //   border: $borderTest;
      font-size: x-large;
      color: rgb(128, 128, 128);
      display: inline-block;

      &:before {
        content: '\2605';
      }

      &.on {
        color: $color-star;
      }

      &.half:after {
        content: '\2605';
        color: $color-star;
        position: absolute;
        margin-left: -20px;
        overflow-x: hidden;
        width: 10px;
      }
    }
  }

  &__slide {
    display: flex;
    justify-content: center;
    align-items: center;
    position: absolute;
    transition: transform 500ms ease-in-out 200ms;
    &.A {
      transform: translateX(-140em);
      //   margin-right: 1em;
    }

    &.B {
      transform: translateX(0);
    }
  }
}
